import React from 'react';
import { motion } from 'framer-motion';
import { ShoppingCart } from 'lucide-react';
import { formatCurrency } from '../utils/helpers';

const ProductCard = ({ product, onAddToCart, isInCart, getItemQuantity }) => {
  const handleAddToCartClick = () => {
    if (!isInCart(product.id)) {
      onAddToCart(product);
    }
  };

  const quantity = getItemQuantity(product.id);
  const isAdded = isInCart(product.id);

  return (
    <motion.div
      className="bg-white rounded-xl shadow-lg overflow-hidden border border-gray-200/50 flex flex-col"
      initial={{ opacity: 0, y: 50 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
      whileHover={{ scale: 1.03, boxShadow: "0 10px 20px rgba(0,0,0,0.1)" }}
    >
      <div className="relative h-48 overflow-hidden">
        <img
          src={product.image}
          alt={product.name}
          className="w-full h-full object-cover object-center"
        />
        {quantity > 0 && (
          <div className="absolute top-2 left-2 bg-blue-500 text-white text-xs font-bold px-2 py-1 rounded-full">
            {quantity}
          </div>
        )}
        <div className="absolute inset-0 bg-gradient-to-t from-black/40 to-transparent"></div>
        <div className="absolute bottom-4 left-4">
          <span className="text-white text-2xl font-bold drop-shadow-md">
            {formatCurrency(product.price)}
          </span>
        </div>
      </div>
      <div className="p-6 flex flex-col flex-grow">
        <h3 className="text-xl font-semibold text-gray-800 mb-2">{product.name}</h3>
        <p className="text-gray-600 text-sm mb-4 flex-grow">{product.description}</p>
        <div className="flex items-center mb-2">
          {[...Array(5)].map((_, i) => (
            <svg
              key={i}
              className={`w-4 h-4 ${i < Math.floor(product.rating) ? 'text-yellow-400' : 'text-gray-300'}`}
              fill="currentColor"
              viewBox="0 0 20 20"
            >
              <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z" />
            </svg>
          ))}
          <span className="ml-2 text-sm text-gray-600">{product.rating}/5</span>
        </div>
        <p className="text-sm text-gray-500 mb-4">Stock: {product.stock}</p>
        <motion.button
          onClick={handleAddToCartClick}
          disabled={isAdded || product.stock === 0}
          className={`mt-auto bg-gradient-to-r ${isAdded ? 'from-green-500 to-green-600' : product.stock === 0 ? 'from-gray-400 to-gray-500' : 'from-blue-500 to-purple-600'} text-white py-3 px-6 rounded-lg font-semibold flex items-center justify-center gap-2 shadow-md hover:shadow-lg transition-all duration-300`}
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
        >
          <ShoppingCart className="w-5 h-5" />
          {isAdded ? 'En carrito' : product.stock === 0 ? 'Sin stock' : 'Agregar al carrito'}
        </motion.button>
      </div>
    </motion.div>
  );
};

export default ProductCard;
