import React, { useState, useEffect, useMemo } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { ShoppingCart, Store } from 'lucide-react';
import ProductCard from './components/ProductCard';
import CartItem from './components/CartItem';
import CartSummary from './components/CartSummary';
import CheckoutForm from './components/CheckoutForm';
import PaymentGateway from './components/PaymentGateway';
import ProductFilters from './components/ProductFilters';
import { defaultProducts } from './mock/products';
import { calculateCartTotals } from './utils/helpers';

const App = () => {
  const [products] = useState(defaultProducts);
  const [cartItems, setCartItems] = useState([]);
  const [view, setView] = useState('products'); // 'products', 'cart', 'checkout', 'payment'
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');

  useEffect(() => {
    const storedCart = localStorage.getItem('shopcart_items');
    if (storedCart) {
      setCartItems(JSON.parse(storedCart));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('shopcart_items', JSON.stringify(cartItems));
  }, [cartItems]);

  const handleAddToCart = (productToAdd) => {
    setCartItems((prevItems) => {
      const existingItem = prevItems.find((item) => item.id === productToAdd.id);
      if (existingItem) {
        return prevItems.map((item) =>
          item.id === productToAdd.id ? { ...item, quantity: item.quantity + 1 } : item
        );
      } else {
        return [...prevItems, { ...productToAdd, quantity: 1 }];
      }
    });
  };

  const handleUpdateQuantity = (id, newQuantity) => {
    if (newQuantity <= 0) {
      handleRemoveItem(id);
      return;
    }
    setCartItems((prevItems) =>
      prevItems.map((item) => (item.id === id ? { ...item, quantity: newQuantity } : item))
    );
  };

  const handleRemoveItem = (id) => {
    setCartItems((prevItems) => prevItems.filter((item) => item.id !== id));
  };

  const handlePaymentSuccess = () => {
    setCartItems([]); // Vaciar el carrito después de la compra exitosa
    setView('products'); // Volver a la vista de productos
  };

  const handlePaymentError = (message) => {
    console.error("Error de pago:", message);
    // Aquí podrías mostrar un mensaje al usuario
  };

  const { subtotal, taxes, total } = calculateCartTotals(cartItems);

  const categories = useMemo(() => {
    const uniqueCategories = [...new Set(products.map(p => p.category))];
    return uniqueCategories;
  }, [products]);

  const filteredProducts = useMemo(() => {
    return products.filter(product => {
      const matchesSearch = product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                            product.description.toLowerCase().includes(searchTerm.toLowerCase());
      const matchesCategory = selectedCategory === 'all' || product.category === selectedCategory;
      return matchesSearch && matchesCategory;
    });
  }, [products, searchTerm, selectedCategory]);

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <motion.header
          className="bg-white shadow-md p-4 mb-8 flex flex-col md:flex-row items-center justify-between gap-6"
          initial={{ opacity: 0, y: -50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
          <div className="flex items-center gap-4">
            <div className="p-2 bg-gray-200 rounded-lg">
              <ShoppingCart className="w-6 h-6 text-gray-700" />
            </div>
            <div>
              <h1 className="text-2xl font-bold text-gray-900">ShopCart</h1>
              <p className="text-gray-600 text-sm">Tu destino de compras virtual</p>
            </div>
          </div>
          <nav className="flex gap-4">
            <motion.button
              onClick={() => setView('products')}
              className={`px-4 py-2 rounded-lg font-medium flex items-center gap-2 ${
                view === 'products'
                  ? 'bg-gray-200 text-gray-900'
                  : 'text-gray-700 hover:bg-gray-100'
              }`}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              <Store className="w-5 h-5" />
              Productos
            </motion.button>
            <motion.button
              onClick={() => setView('cart')}
              className={`px-4 py-2 rounded-lg font-medium flex items-center gap-2 ${
                view === 'cart'
                  ? 'bg-gray-200 text-gray-900'
                  : 'text-gray-700 hover:bg-gray-100'
              }`}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              <ShoppingCart className="w-5 h-5" />
              Carrito ({cartItems.length})
            </motion.button>
          </nav>
        </motion.header>

        <AnimatePresence mode="wait">
          {view === 'products' && (
            <motion.div
              key="products"
              initial={{ opacity: 0, x: -50 }}
              animate={{ opacity: 1, x: 0 }}
              exit={{ opacity: 0, x: 50 }}
              transition={{ duration: 0.5 }}
            >
              <ProductFilters
                searchTerm={searchTerm}
                onSearchChange={setSearchTerm}
                selectedCategory={selectedCategory}
                onCategoryChange={setSelectedCategory}
                categories={categories}
              />
              <div className="mb-8">
                <h2 className="text-3xl font-bold text-gray-900 mb-2">
                  {selectedCategory === 'all' ? 'Todos los Productos' : selectedCategory}
                </h2>
                <p className="text-gray-600">
                  {filteredProducts.length} producto{filteredProducts.length !== 1 ? 's' : ''}{' '}
                  {searchTerm && `encontrado${filteredProducts.length !== 1 ? 's' : ''} para "${searchTerm}"`}
                </p>
              </div>
              <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
                {filteredProducts.length > 0 ? (
                  filteredProducts.map((product) => (
                    <ProductCard key={product.id} product={product} onAddToCart={handleAddToCart} />
                  ))
                ) : (
                  <div className="text-center py-16">
                    <div className="text-gray-400 mb-4">
                      <svg className="w-16 h-16 mx-auto" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1} d="M9.172 16.172a4 4 0 015.656 0M9 12h6m-6-4h6m2 5.291A7.962 7.962 0 0112 15c-2.34 0-4.467-.881-6.08-2.33M15 13a3 3 0 11-6 0 3 3 0 016 0z" />
                      </svg>
                    </div>
                    <h3 className="text-lg font-medium text-gray-900 mb-2">No se encontraron productos</h3>
                    <p className="text-gray-500">Intenta con otros términos de búsqueda o categorías.</p>
                  </div>
                )}
              </div>
            </motion.div>
          )}

          {view === 'cart' && (
            <motion.div
              key="cart"
              initial={{ opacity: 0, x: -50 }}
              animate={{ opacity: 1, x: 0 }}
              exit={{ opacity: 0, x: 50 }}
              transition={{ duration: 0.5 }}
              className="grid grid-cols-1 lg:grid-cols-3 gap-8"
            >
              <div className="lg:col-span-2 space-y-6">
                {cartItems.length === 0 ? (
                  <div className="text-center py-16">
                    <div className="text-gray-400 mb-4">
                      <ShoppingCart className="w-16 h-16 mx-auto" />
                    </div>
                    <h3 className="text-lg font-medium text-gray-900 mb-2">Tu carrito está vacío</h3>
                    <p className="text-gray-500">¡Parece que no has añadido nada aún! Explora nuestros productos.</p>
                    <motion.button
                      onClick={() => setView('products')}
                      className="mt-6 bg-gray-200 text-gray-900 py-2 px-4 rounded-lg font-medium hover:bg-gray-300 transition-colors"
                      whileHover={{ scale: 1.05 }}
                      whileTap={{ scale: 0.95 }}
                    >
                      Ir a la Tienda
                    </motion.button>
                  </div>
                ) : (
                  <AnimatePresence>
                    {cartItems.map((item) => (
                      <CartItem
                        key={item.id}
                        item={item}
                        onUpdateQuantity={handleUpdateQuantity}
                        onRemoveItem={handleRemoveItem}
                      />
                    ))}
                  </AnimatePresence>
                )}
              </div>
              {cartItems.length > 0 && (
                <div className="lg:col-span-1">
                  <CartSummary
                    subtotal={subtotal}
                    taxes={taxes}
                    total={total}
                    onCheckout={() => setView('checkout')}
                  />
                </div>
              )}
            </motion.div>
          )}

          {view === 'checkout' && (
            <motion.div
              key="checkout"
              initial={{ opacity: 0, x: -50 }}
              animate={{ opacity: 1, x: 0 }}
              exit={{ opacity: 0, x: 50 }}
              transition={{ duration: 0.5 }}
              className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8"
            >
              <CheckoutForm
                onConfirmPurchase={() => setView('payment')}
                onBackToCart={() => setView('cart')}
              />
            </motion.div>
          )}

          {view === 'payment' && (
            <motion.div
              key="payment"
              initial={{ opacity: 0, x: -50 }}
              animate={{ opacity: 1, x: 0 }}
              exit={{ opacity: 0, x: 50 }}
              transition={{ duration: 0.5 }}
              className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8"
            >
              <PaymentGateway
                total={total}
                onPaymentSuccess={handlePaymentSuccess}
                onPaymentError={handlePaymentError}
                onBackToCheckout={() => setView('checkout')}
              />
            </motion.div>
          )}
        </AnimatePresence>
      </div>
    </div>
  );
};

export default App;
