import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { ShoppingCart, Check } from 'lucide-react';
import { formatCurrency } from '../utils/helpers';

const ProductCard = ({ product, onAddToCart }) => {
  const [isAdded, setIsAdded] = useState(false);

  const handleAddToCart = () => {
    onAddToCart(product);
    setIsAdded(true);
    // Resetear después de 2 segundos (opcional)
   // setTimeout(() => setIsAdded(false), 2000);
  };

  return (
    <motion.div
      className="bg-white rounded-xl shadow-lg overflow-hidden border border-gray-200/50 flex flex-col"
      initial={{ opacity: 0, y: 50 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
      whileHover={{ scale: 1.03, boxShadow: "0 10px 20px rgba(0,0,0,0.1)" }}
    >
      <div className="relative h-48 overflow-hidden">
        <img
          src={product.image}
          alt={product.name}
          className="w-full h-full object-cover object-center"
        />
        <div className="absolute inset-0 bg-gradient-to-t from-black/40 to-transparent"></div>
      </div>
      <div className="p-6 flex flex-col flex-grow">
        <div className="flex items-center justify-between mb-2">
          <span className="text-sm text-blue-600 font-medium bg-blue-50 px-2 py-1 rounded">{product.category}</span>
          <div className="flex items-center space-x-1">
            {/* Íconos de rating... */}
          </div>
        </div>
        <h3 className="text-xl font-semibold text-gray-800 mb-2">{product.name}</h3>
        <p className="text-gray-600 text-sm mb-4 flex-grow">{product.description}</p>
        <div className="flex items-center justify-between mb-2">
          <span className="text-black text-xl font-bold drop-shadow-md">
            {formatCurrency(product.price)}
          </span>
          <div className="flex items-center justify-between text-gray-500 text-base mb-4">
            <span>Disponibles: {product.stock}</span>
          </div>
        </div>
        <motion.button
          onClick={handleAddToCart}
          className={`mt-auto py-3 px-6 rounded-lg font-semibold flex items-center justify-center gap-2 shadow-md hover:shadow-lg transition-all duration-300 ${
            isAdded 
              ? 'bg-green-500 text-white' 
              : 'bg-gradient-to-r from-blue-500 to-purple-600 text-white'
          }`}
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
        >
          {isAdded ? (
            <>
              <Check className="w-5 h-5" />
              En carrito
            </>
          ) : (
            <>
              <ShoppingCart className="w-5 h-5" />
              Agregar al Carrito
            </>
          )}
        </motion.button>
      </div>
    </motion.div>
  );
};

export default ProductCard;
